mapper.select:
SELECT 
	
FROM zl_cart a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.id=#{id}


	AND a.oppen_id=#{oppenId}


	AND a.goods_id=#{goodsId}

SELECT
FROM zl_cart a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.goods_name LIKE '%'||#{goodsName}||'%' '%'+#{goodsName}+'%' concat('%',#{goodsName},'%')
AND a.oppen_id LIKE '%'||#{oppenId}||'%' '%'+#{oppenId}+'%' concat('%',#{oppenId},'%')
ORDER BY ${page.orderBy}

mapper.update:
UPDATE zl_cart SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE goods_id = #{goodsId}

mapper.select:
select IFNULL(sum(binary(goods_total)),0) from zl_cart where oppen_id=#{oppenId}
and del_flag='0'

  and goods_id in(${goodsId})
